import { useState } from "react";
import { Link } from "react-router-dom";
import "./loginstyle.css";
import bcrypt from "bcryptjs";
const express = require("express");
const Client = require("src/models/Client/client.model.js");

const app = express();
const port = 3000;

app.get("/api/users", async (req, res) => {
  const { username } = req.query;
  const user = await User.findOne({ where: { username } });
  res.json(user);
});

app.listen(port, () => {
  console.log(`Server listening at http://localhost:${port}`);
});

const LoginPage = (props) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!username || !password) {
      alert("Username and password are required!");
      return;
    }

    fetch(`/api/client?clientusername=${username}`)
      .then((response) => response.json())
      .then((user) => {
        // Check that the retrieved user's password matches the provided password
        if (user && bcrypt.compareSync(password, user.password)) {
          // Authenticate the user by setting a session or JWT token
          props.onLogin(user);
          alert("Login successful!");
        } else {
          // Display an error message if the login information is incorrect
          alert("Incorrect username or password");
        }
      })
      .catch((error) => console.error(error));
    // login user logic goes here
    // check if a user exists already in DB-> WH else throw error
    // redirect to home page on successful login
    // history.push('/'); (use this if you are using react-router-dom)
    // window.location.href = '/'; (use this to redirect manually)
  };

  return (
    <div>
      <h1>Login</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Username:
          <input
            type="text"
            value={username}
            onChange={(event) => setUsername(event.target.value)}
          />
        </label>
        <br />
        <label>
          Password:
          <input
            type="password"
            value={password}
            onChange={(event) => setPassword(event.target.value)}
          />
        </label>
        <br />
        <button type="submit">Log In</button>
      </form>
      <p>
        Don't have an account? <Link to="/register">Sign up here</Link>.
      </p>
    </div>
  );
};

export default LoginPage;
