import { useState } from "react";
import { Link } from "react-router-dom";
import "./loginstyle.css";
const express = require("express");
const bodyParser = require("body-parser");
const { Client } = require("src/models/Client");
const { sequelize, Sequelize } = require("sequelize");
sequelize = new Sequelize("postgres", "postgres", "hkateliev99", {
  host: "localhost",
  dialect: "postgres",
  port: 5434,
});

const app = express();
const port = 3000;
app.use(bodyParser.json());

sequelize
  .authenticate()
  .then(() => {
    console.log("Connection has been established successfully.");
  })
  .catch((error) => {
    console.error("Unable to connect to the database: ", error);
  });

const LoginPage = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!username || !password) {
      alert("Username and password are required!");
      return;
    }

    try {
      const response = await fetch("/api/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          username,
          password,
        }),
      });

      if (response.ok) {
        // Successful login
        // Redirect to home page
        history.push("/");
      } else {
        // Invalid credentials
        // Display error message
        const errorData = await response.json();
        alert(errorData.message);
      }
    } catch (error) {
      console.error(error);
      alert("Something went wrong");
    }
    // login user logic goes here
    // check if a user exists already in DB-> WH else throw error
    // redirect to home page on successful login
    // history.push('/'); (use this if you are using react-router-dom)
    // window.location.href = '/'; (use this to redirect manually)
  };
  // Start server
  app.listen(port, () => {
    console.log(`Server listening on port ${port}`);
  });
  return (
    <div>
      <h1>Login</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Username:
          <input
            type="text"
            value={username}
            onChange={(event) => setUsername(event.target.value)}
          />
        </label>
        <br />
        <label>
          Password:
          <input
            type="password"
            value={password}
            onChange={(event) => setPassword(event.target.value)}
          />
        </label>
        <br />
        <button type="submit">Log In</button>
      </form>
      <p>
        Don't have an account? <Link to="/register">Sign up here</Link>.
      </p>
    </div>
  );
};

export default LoginPage;
